if ($input.item.json.body) {
    $input.item.json.content = $input.item.json.body
      // Remove <style>, <script>, and other head-like content blocks
      .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
      .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
      .replace(/<noscript[^>]*>[\s\S]*?<\/noscript>/gi, '')
      .replace(/<!--[\s\S]*?-->/g, '') // HTML comments

      // Remove common non-content elements
      .replace(/<nav[^>]*>[\s\S]*?<\/nav>/gi, '')
      .replace(/<header[^>]*>[\s\S]*?<\/header>/gi, '')
      .replace(/<footer[^>]*>[\s\S]*?<\/footer>/gi, '')
      .replace(/<aside[^>]*>[\s\S]*?<\/aside>/gi, '')

      // Convert common HTML entities before removing tags
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>')
      .replace(/&quot;/g, '"')
      .replace(/&#39;/g, "'")
      .replace(/&mdash;/g, '_')
      .replace(/&ndash;/g, '_')

      // Remove all HTML tags but preserve line breaks for paragraphs
      .replace(/<\/?(p|div|br|h[1-6])[^>]*>/gi, '\n')
      .replace(/<[^<]+?>/g, '')

      // Remove CSS or JS snippets
      .replace(/(?:\.([\w-]+|#[\w-]+)?\s*\{[^}]*\})/g, '')             // CSS rules
      .replace(/function\s+\w+\s*\([^)]*\)\s*\{[^}]+\}/g, '')          // JS functions
      .replace(/var\s+\w+\s*=[\s\S]*?;/g, '')                         // Variable declarations

      // Remove UI junk and boilerplate content phrases
      .replace(/\b(Subscribe|Newsletter|Book a (call|meeting)|Leave a Reply|Cancel Reply|Login|Logout|Sign\s?up|Sign\s?in|Your Name|Email Address|Website|Privacy Policy|Terms (of Service|& Conditions)|Close Menu|Skip to (main )?content|Back to top|Search|Menu|Share|Tweet|Pin|Read More|Continue Reading|Related Posts?|Tags?:|Categories?:)\b/gi, '')

      // Remove social and footer keywords
      .replace(/\b(Facebook|Twitter|LinkedIn|Instagram|YouTube|TikTok|© \d{4} [\w\s.]+|All Rights Reserved)\b/gi, '')

      // Remove navigation and pagination text
      .replace(/\b(Next|Previous|Page \d+( of \d+)?|Home|About|Contact|Blog)\b/gi, '')

      // Remove common Medium-specific elements
      .replace(/\b(clap|responses?|follow|member-only story|this story is published in|get the medium app)\b/gi, '')

      // Clean up whitespace and formatting
      .replace(/\r?\n|\r/g, ' ')              // Remove all newlines first
      .replace(/\s{2,}/g, ' ')                // Collapse multiple spaces
      .replace(/\s+([.!?])/g, '$1')           // Remove space before punctuation
      .trim();

    // Optional: Set a minimum content length filter
    if ($input.item.json.content.length < 50) {
      $input.item.json.content = "Content too short or unable to extract meaningful text";
    }

    // Optional: Add word count
    $input.item.json.wordCount = $input.item.json.content.split(/\s+/).filter(word => word.length > 0).length;
}

return $input.item;

const content = $input.item.json.content;

const regex = /([^\[\]\n]+?)\s*\[[^\[\]]+\]Até\s*(\d+)pontospor R\$/g;
let match;
let results = [];

while ((match = regex.exec(content)) !== null) {
  let company = match[1].trim();

  // Remove common unwanted prefixes
  company = company.replace(/^(NovidadesPromoçãoLogo|PromoçãoLogo|Logo)\s*/i, '');

  // Split company into words, handle cases where there are multiple spaces
  const words = company.split(/\s+/);

  if (words.length > 5) {
    company = words.slice(-3).join(' ');
  }

  const points = parseInt(match[2], 10);
  results.push({ company, points });
}

// SORT: By points, decreasing order
results.sort((a, b) => b.points - a.points);

$input.item.json.companiesAndPoints = { results };
return $input.item.json.companiesAndPoints;

const results = $input.first().json.results;

// Get today's date in dd/mm/YYYY
const today = new Date();
const dd = String(today.getDate()).padStart(2, '0');
const mm = String(today.getMonth() + 1).padStart(2, '0'); // January is 0!
const yyyy = today.getFullYear();
const dateStr = `${dd}/${mm}/${yyyy}`;

// Prepare formatted message
let message = `Empresas que tão mais pagando na LIVELO (${dateStr})\n\n`;
results.forEach((entry, idx) => { //.slice(0, 60)
  message += `${idx + 1}. ${entry.company}: ${entry.points} pts/R$\n`;
});

return {data: message.trim()};
